mapper.select:
SELECT * FROM BOOK_TBL 

JOIN SURVEY_TBL ON BOOK_CATEGORY LIKE '%' || '액션' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }


JOIN SURVEY_TBL ON BOOK_CATEGORY LIKE '%' || '뷰티' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }


JOIN SURVEY_TBL ON BOOK_CATEGORY LIKE '%' || '어린이' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }


JOIN SURVEY_TBL ON BOOK_CATEGORY LIKE '%' || '유아' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }


JOIN SURVEY_TBL ON BOOK_CATEGORY LIKE '%' || '요리' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }


JOIN SURVEY_TBL ON BOOK_CATEGORY LIKE '%' || '여행' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }


JOIN SURVEY_TBL ON BOOK_CATEGORY LIKE '%' || '음악' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }


JOIN SURVEY_TBL ON BOOK_WRITER LIKE '%' || '#{answerWriter}' || '%' WHERE SURVEY_TBL.USER_ID = #{userId }
